import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [Movies, setFetchMovie] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  // we can use the async and await way to achieve the same .then method
  async function fetchMovieHandler() {
    setIsLoading(true);
    const response = await fetch("https://swapi.dev/api/films");

    const data = await response.json();
    const transformedMovies = data.results.map((filmData) => {
      return {
        id: filmData.episode_id,
        openingText: filmData.opening_crawl,
        title: filmData.title,
        releaseDate: filmData.release_date,
      };
    });
    setFetchMovie(transformedMovies);
    setTimeout(() => {}, 3000);
    setIsLoading(false);
  }
  /////////////////////////
  // function fetchMovieHandler() {
  //   setIsLoading(true);
  //   fetch("https://swapi.dev/api/films")
  //     .then((response) => {
  //       return response.json();
  //     })
  //     .then((data) => {
  //       const transformedMovies = data.results.map((filmData) => {
  //         return {
  //           id: filmData.episode_id,
  //           openingText: filmData.opening_crawl,
  //           title: filmData.title,
  //           releaseDate: filmData.release_date,
  //         };
  //       });
  //       setFetchMovie(transformedMovies);
  //     });
  //   setIsLoading(false);
  // }
  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieHandler}> Fetch Movies </button>
      </section>
      <section>
        {!isLoading && Movies.length > 0 ? (
          <MoviesList movies={Movies} />
        ) : (
          <p>Loading...</p>
        )}
        {Movies.length <= 0 ? <p>No Movies to show...</p> : ""}
      </section>
    </React.Fragment>
  );
}

export default App;
